function Init(){document.hashform.domain.value="";document.hashform.sitePassword.value="";document.hashform.hashedPassword.value="";document.hashform.hashedPassword.disabled=true}var SPH_kPasswordPrefix="@@";function Generate(){var a=document.hashform.domain.value;var b=(new SPH_DomainExtractor()).extractDomain(a);var c=SPH_kPasswordPrefix.length;var d=document.hashform.sitePassword.value;if(d.substring(0,c)==SPH_kPasswordPrefix)d=d.substring(c);var e=new String(new SPH_HashedPassword(d,b));return e}function GenerateToTextField(){document.hashform.hashedPassword.value=Generate();document.hashform.hashedPassword.disabled=false}